	`createdAt` timestamp NOT NULL DEFAULT (now()),
	CONSTRAINT `integration_guides_id` PRIMARY KEY(`id`),
	CONSTRAINT `integration_guides_teacherId_unique` UNIQUE(`teacherId`)
);

